import { useEffect, useState, useRef } from "react";
import { v4 as uuidv4 } from "uuid";
import axios from "axios";
import { 
  Send, 
  Plus, 
  Bot, 
  User, 
  Loader2, 
  Sparkles,
  MessageSquare,
  Trash2,
  Copy,
  Check
} from "lucide-react";

function Chat() {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");
  const [loading, setLoading] = useState(false);
  const [copiedIndex, setCopiedIndex] = useState(null);
  const messagesEndRef = useRef(null);
  const inputRef = useRef(null);

  const [sessionId, setSessionId] = useState(() => {
    const stored = localStorage.getItem("sessionId");
    if (stored) return stored;
    const newId = uuidv4();
    localStorage.setItem("sessionId", newId);
    return newId;
  });

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  useEffect(() => {
    axios
      .get(`http://localhost:5000/api/sessions/${sessionId}`)
      .then(res => setMessages(res.data))
      .catch(() => {});
  }, [sessionId]);

  useEffect(() => {
    // Focus input on mount
    inputRef.current?.focus();
  }, []);

  const sendMessage = async () => {
    if (!input.trim()) return;

    setLoading(true);
    const userMessage = { role: "user", content: input };
    setMessages(prev => [...prev, userMessage]);
    setInput("");

    try {
      const res = await axios.post(
        "http://localhost:5000/api/chat",
        { sessionId, message: input }
      );

      const assistantMessage = {
        role: "assistant",
        content: res.data.reply
      };

      setMessages(prev => [...prev, assistantMessage]);
    } catch (error) {
      alert("Error generating response");
    } finally {
      setLoading(false);
      inputRef.current?.focus();
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === "Enter" && !e.shiftKey) {
      e.preventDefault();
      sendMessage();
    }
  };

  const newChat = () => {
    const newId = uuidv4();
    localStorage.setItem("sessionId", newId);
    setSessionId(newId);
    setMessages([]);
  };

  const copyToClipboard = async (text, index) => {
    await navigator.clipboard.writeText(text);
    setCopiedIndex(index);
    setTimeout(() => setCopiedIndex(null), 2000);
  };

  const formatMessage = (content) => {
    // Simple markdown-like formatting
    return content.split('\n').map((line, i) => {
      if (line.startsWith('```') && line.endsWith('```')) {
        return (
          <pre key={i} className="bg-gray-800 text-gray-100 p-3 rounded-lg overflow-x-auto my-2">
            <code>{line.slice(3, -3)}</code>
          </pre>
        );
      }
      if (line.startsWith('- ')) {
        return <li key={i} className="ml-4">â€¢ {line.slice(2)}</li>;
      }
      if (line.match(/^\d+\./)) {
        return <li key={i} className="ml-4">{line}</li>;
      }
      return <p key={i} className="mb-2">{line}</p>;
    });
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-900 to-gray-800 flex items-center justify-center p-4">
      <div className="w-full max-w-4xl bg-gray-800 rounded-2xl shadow-2xl overflow-hidden border border-gray-700">
        {/* Header */}
        <div className="bg-gradient-to-r from-purple-600 to-blue-600 p-4 flex items-center justify-between">
          <div className="flex items-center space-x-3">
            <div className="bg-white/20 p-2 rounded-xl backdrop-blur">
              <Bot className="w-6 h-6 text-white" />
            </div>
            <div>
              <h2 className="text-xl font-bold text-white flex items-center">
                AI Support Assistant
                <Sparkles className="w-4 h-4 ml-2 text-yellow-300" />
              </h2>
              <p className="text-xs text-purple-200">Powered by Advanced AI</p>
            </div>
          </div>
          <button
            onClick={newChat}
            className="flex items-center space-x-2 px-4 py-2 bg-white/10 hover:bg-white/20 rounded-xl transition-all duration-200 text-white backdrop-blur border border-white/10"
          >
            <Plus className="w-4 h-4" />
            <span>New Chat</span>
          </button>
        </div>

        {/* Messages Area */}
        <div className="h-[500px] overflow-y-auto p-4 space-y-4 bg-gray-800/50">
          {messages.length === 0 ? (
            <div className="h-full flex flex-col items-center justify-center text-gray-400">
              <MessageSquare className="w-16 h-16 mb-4 opacity-50" />
              <p className="text-lg font-medium">Start a conversation</p>
              <p className="text-sm">Ask me anything about our products or services</p>
            </div>
          ) : (
            messages.map((m, i) => (
              <div
                key={i}
                className={`flex ${m.role === "user" ? "justify-end" : "justify-start"} group`}
              >
                <div
                  className={`max-w-[80%] rounded-2xl p-4 ${
                    m.role === "user"
                      ? "bg-gradient-to-r from-purple-600 to-blue-600 text-white"
                      : "bg-gray-700 text-gray-100 border border-gray-600"
                  }`}
                >
                  <div className="flex items-center space-x-2 mb-2">
                    <div className={`p-1 rounded-full ${
                      m.role === "user" ? "bg-white/20" : "bg-purple-500/20"
                    }`}>
                      {m.role === "user" ? (
                        <User className="w-4 h-4" />
                      ) : (
                        <Bot className="w-4 h-4" />
                      )}
                    </div>
                    <span className="text-sm font-medium">
                      {m.role === "user" ? "You" : "AI Assistant"}
                    </span>
                    <button
                      onClick={() => copyToClipboard(m.content, i)}
                      className="opacity-0 group-hover:opacity-100 transition-opacity ml-2"
                    >
                      {copiedIndex === i ? (
                        <Check className="w-4 h-4 text-green-400" />
                      ) : (
                        <Copy className="w-4 h-4 text-gray-400 hover:text-white" />
                      )}
                    </button>
                  </div>
                  <div className="prose prose-invert max-w-none">
                    {formatMessage(m.content)}
                  </div>
                </div>
              </div>
            ))
          )}

          {loading && (
            <div className="flex justify-start">
              <div className="bg-gray-700 rounded-2xl p-4 border border-gray-600">
                <div className="flex items-center space-x-3">
                  <div className="bg-purple-500/20 p-2 rounded-full">
                    <Bot className="w-4 h-4 text-purple-400" />
                  </div>
                  <div className="flex items-center space-x-2">
                    <Loader2 className="w-4 h-4 text-purple-400 animate-spin" />
                    <span className="text-gray-300">Generating response...</span>
                  </div>
                </div>
              </div>
            </div>
          )}
          <div ref={messagesEndRef} />
        </div>

        {/* Input Area */}
        <div className="p-4 bg-gray-800 border-t border-gray-700">
          <div className="flex items-end space-x-2">
            <div className="flex-1 relative">
              <textarea
                ref={inputRef}
                value={input}
                onChange={(e) => setInput(e.target.value)}
                onKeyDown={handleKeyPress}
                placeholder="Type your message... (Shift+Enter for new line)"
                className="w-full bg-gray-700 text-white rounded-xl pl-4 pr-12 py-3 focus:outline-none focus:ring-2 focus:ring-purple-500 resize-none min-h-[50px] max-h-[200px] border border-gray-600"
                rows="1"
                style={{ height: 'auto', overflowY: 'hidden' }}
                onInput={(e) => {
                  e.target.style.height = 'auto';
                  e.target.style.height = `${Math.min(e.target.scrollHeight, 200)}px`;
                }}
              />
              {input && (
                <button
                  onClick={() => setInput("")}
                  className="absolute right-3 bottom-3 text-gray-400 hover:text-white"
                >
                  <Trash2 className="w-4 h-4" />
                </button>
              )}
            </div>
            <button
              onClick={sendMessage}
              disabled={loading || !input.trim()}
              className={`p-3 rounded-xl transition-all duration-200 ${
                loading || !input.trim()
                  ? "bg-gray-600 cursor-not-allowed"
                  : "bg-gradient-to-r from-purple-600 to-blue-600 hover:from-purple-700 hover:to-blue-700"
              }`}
            >
              {loading ? (
                <Loader2 className="w-5 h-5 text-white animate-spin" />
              ) : (
                <Send className="w-5 h-5 text-white" />
              )}
            </button>
          </div>

          {/* Footer Info */}
          <div className="mt-3 text-xs text-gray-400 flex justify-between items-center">
            <span>Session ID: {sessionId.slice(0, 8)}...</span>
            <span className="flex items-center">
              <Sparkles className="w-3 h-3 mr-1 text-yellow-500" />
              AI-powered responses
            </span>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Chat;
